let buttonBuy=document.querySelectorAll(".buy"),addModal=document.querySelector(".add-item__modal"),closeButton=addModal.querySelector(".cancel__button");for(let t=0;t<buttonBuy.length;t++)buttonBuy[t].addEventListener("click",function(t){t.preventDefault(),addModal.classList.add("modal-show")});closeButton.addEventListener("click",function(){addModal.classList.remove("modal-show")});